import {
    Button,
    Card,
    Form,
    FormLayout,
    Layout,
    Page,
    Stack,
    TextField,
  } from '@shopify/polaris';

  class AnnotatedLayout extends React.Component {
    state = {
      discount: '10%',
    };

    render() {
      const { discount } = this.state;

      return (
        <Page>
          <Layout>
            <Layout.AnnotatedSection
              title="Default interest rate"
              description="This interest rate will be used to calculate the payments of your purchase."
            >
              <Card sectioned>
                <Form onSubmit={this.handleSubmit}>
                  <FormLayout>
                    <TextField
                      value={discount}
                      onChange={this.handleChange('discount')}
                      label="Interest rate"
                      type="discount"
                    />
                    <Stack distribution="trailing">
                      <Button primary submit>
                        Save
                      </Button>
                    </Stack>
                  </FormLayout>
                </Form>
              </Card>
            </Layout.AnnotatedSection>
            <Layout.AnnotatedSection
              title="Default financing period (in years)"
              description="This financing period is the length of the reimboursment period."
            >
              <Card sectioned>
                <Form onSubmit={this.handleSubmit}>
                  <FormLayout>
                    <TextField
                      value={discount}
                      onChange={this.handleChange('discount')}
                      label="Interest rate"
                      type="discount"
                    />
                    <Stack distribution="trailing">
                      <Button primary submit>
                        Save
                      </Button>
                    </Stack>
                  </FormLayout>
                </Form>
              </Card>
            </Layout.AnnotatedSection>
          </Layout>
        </Page>
      );
    }

    handleSubmit = () => {
      this.setState({
        discount: this.state.discount,
      });
      console.log('submission', this.state);
    };

    handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
    };
  }

  export default AnnotatedLayout;
